/**
 * Página de Notificaciones
 * Muestra las notificaciones del estudiante
 */

import React, { useState, useEffect } from 'react';
import {
  Box,
  Container,
  Paper,
  Typography,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  IconButton,
  Button,
  Chip,
  Alert,
} from '@mui/material';
import {
  CheckCircle,
  Cancel,
  Info,
  MarkEmailRead,
  Delete,
  ArrowBack,
  School,
} from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';
import { useAuthStore } from '../store/authStore';
import axios from 'axios';
import toast from 'react-hot-toast';

interface Notificacion {
  id: string;
  tipo: string;
  titulo: string;
  mensaje: string;
  leida: boolean;
  createdAt: string;
}

export const NotificationsPage: React.FC = () => {
  const navigate = useNavigate();
  const { isAuthenticated } = useAuthStore();
  const [notificaciones, setNotificaciones] = useState<Notificacion[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!isAuthenticated) {
      navigate('/login');
    } else {
      loadNotifications();
    }
  }, [isAuthenticated, navigate]);

  const loadNotifications = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('accessToken');
      const response = await axios.get('http://localhost:3001/api/notificaciones/my-notifications', {
        headers: { Authorization: `Bearer ${token}` },
      });

      setNotificaciones(response.data.data.notificaciones);
    } catch (error) {
      toast.error('Error al cargar notificaciones');
    } finally {
      setLoading(false);
    }
  };

  const markAsRead = async (id: string) => {
    try {
      const token = localStorage.getItem('accessToken');
      await axios.patch(
        `http://localhost:3001/api/notificaciones/${id}/read`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setNotificaciones((prev) =>
        prev.map((n) => (n.id === id ? { ...n, leida: true } : n))
      );

      toast.success('Notificación marcada como leída');
    } catch (error) {
      toast.error('Error al marcar notificación');
    }
  };

  const markAllAsRead = async () => {
    try {
      const token = localStorage.getItem('accessToken');
      await axios.patch(
        'http://localhost:3001/api/notificaciones/mark-all-read',
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setNotificaciones((prev) => prev.map((n) => ({ ...n, leida: true })));
      toast.success('Todas las notificaciones marcadas como leídas');
    } catch (error) {
      toast.error('Error al marcar notificaciones');
    }
  };

  const getIcon = (tipo: string) => {
    switch (tipo) {
      case 'DOCUMENTO_APROBADO':
        return <CheckCircle sx={{ color: 'success.main' }} />;
      case 'DOCUMENTO_RECHAZADO':
        return <Cancel sx={{ color: 'error.main' }} />;
      case 'DOCUMENTO_PENDIENTE':
        return <Info sx={{ color: 'warning.main' }} />;
      default:
        return <Info sx={{ color: 'info.main' }} />;
    }
  };

  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleString('es-MX', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  const unreadCount = notificaciones.filter((n) => !n.leida).length;

  return (
    <Box sx={{ minHeight: '100vh', backgroundColor: '#FFFFFF' }}>
      {/* Header */}
      <Box
        sx={{
          background: 'linear-gradient(135deg, #008000 0%, #006000 100%)',
          color: '#FFFFFF',
          py: 3,
          mb: 4,
        }}
      >
        <Container>
          <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
            <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
              <IconButton color="inherit" onClick={() => navigate('/dashboard')}>
                <ArrowBack />
              </IconButton>
              <School sx={{ fontSize: 40 }} />
              <Typography variant="h4" fontWeight="bold">
                Notificaciones
              </Typography>
            </Box>
            {unreadCount > 0 && (
              <Button
                variant="outlined"
                color="inherit"
                startIcon={<MarkEmailRead />}
                onClick={markAllAsRead}
              >
                Marcar todas como leídas ({unreadCount})
              </Button>
            )}
          </Box>
        </Container>
      </Box>

      <Container>
        {loading ? (
          <Paper sx={{ p: 3, textAlign: 'center' }}>
            <Typography>Cargando notificaciones...</Typography>
          </Paper>
        ) : notificaciones.length === 0 ? (
          <Paper sx={{ p: 4, textAlign: 'center' }}>
            <Info sx={{ fontSize: 60, color: 'text.secondary', mb: 2 }} />
            <Typography variant="h6" color="text.secondary">
              No tienes notificaciones
            </Typography>
          </Paper>
        ) : (
          <Paper sx={{ borderRadius: 2 }}>
            <List>
              {notificaciones.map((notif, index) => (
                <React.Fragment key={notif.id}>
                  <ListItem
                    sx={{
                      bgcolor: notif.leida ? 'transparent' : '#f0f7ff',
                      '&:hover': { bgcolor: notif.leida ? '#fafafa' : '#e3f2fd' },
                    }}
                    secondaryAction={
                      !notif.leida && (
                        <IconButton edge="end" onClick={() => markAsRead(notif.id)}>
                          <MarkEmailRead />
                        </IconButton>
                      )
                    }
                  >
                    <ListItemIcon>{getIcon(notif.tipo)}</ListItemIcon>
                    <ListItemText
                      primary={
                        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                          <Typography variant="subtitle1" fontWeight={notif.leida ? 'normal' : 'bold'}>
                            {notif.titulo}
                          </Typography>
                          {!notif.leida && (
                            <Chip label="Nueva" size="small" color="primary" />
                          )}
                        </Box>
                      }
                      secondary={
                        <>
                          <Typography variant="body2" color="text.primary" sx={{ mt: 0.5 }}>
                            {notif.mensaje}
                          </Typography>
                          <Typography variant="caption" color="text.secondary" sx={{ mt: 0.5, display: 'block' }}>
                            {formatDate(notif.createdAt)}
                          </Typography>
                        </>
                      }
                    />
                  </ListItem>
                  {index < notificaciones.length - 1 && <Box sx={{ borderBottom: 1, borderColor: 'divider' }} />}
                </React.Fragment>
              ))}
            </List>
          </Paper>
        )}
      </Container>
    </Box>
  );
};

export default NotificationsPage;
